import React, { useState } from 'react';

function ImageWithFallback(props) {
    const { src, alt, className, style, fallbackSrc = '/logo192.png' } = props;
    const [error, setError] = useState(false);

    const handleError = () => {
        if (!error) {
            setError(true);
        }
    };

    return (
        <img
            src={error || !src ? fallbackSrc : src}
            alt={alt || ''}
            className={className}
            style={style}
            onError={handleError}
            loading="lazy"
        />
    );
}

export default ImageWithFallback;
