import React, { useState, useRef, useEffect } from 'react';
import './ARViewer.css';

function ARViewer({ product }) {
    const [isARMode, setIsARMode] = useState(false);
    const [isVRMode, setIsVRMode] = useState(false);
    const [currentView, setCurrentView] = useState('exterior');
    const [isLoading, setIsLoading] = useState(false);
    const canvasRef = useRef(null);
    const videoRef = useRef(null);

    // Mock 3D model data - s·∫Ω ƒë∆∞·ª£c thay th·∫ø b·∫±ng data th·ª±c t·∫ø t·ª´ backend
    const modelData = {
        exterior: {
            name: 'Exterior View',
            description: 'Xem b√™n ngo√†i thi·∫øt b·ªã',
            components: [
                { name: 'M√†n h√¨nh', color: '#000000', position: { x: 0, y: 0, z: 0 } },
                { name: 'Khung m√°y', color: '#C0C0C0', position: { x: 0, y: 0, z: -1 } },
                { name: 'Camera', color: '#FF0000', position: { x: 0, y: 0.5, z: 0.5 } },
                { name: 'N√∫t ngu·ªìn', color: '#808080', position: { x: 0.3, y: 0, z: 0 } }
            ]
        },
        interior: {
            name: 'Interior View',
            description: 'Xem b√™n trong thi·∫øt b·ªã',
            components: [
                { name: 'CPU', color: '#00FF00', position: { x: 0, y: 0, z: 0 } },
                { name: 'RAM', color: '#0000FF', position: { x: 0.2, y: 0, z: 0 } },
                { name: 'Storage', color: '#FFFF00', position: { x: -0.2, y: 0, z: 0 } },
                { name: 'Battery', color: '#FF00FF', position: { x: 0, y: -0.3, z: 0 } },
                { name: 'Motherboard', color: '#00FFFF', position: { x: 0, y: 0, z: -0.1 } }
            ]
        },
        exploded: {
            name: 'Exploded View',
            description: 'Xem c√°c b·ªô ph·∫≠n t√°ch r·ªùi',
            components: [
                { name: 'Screen Assembly', color: '#000000', position: { x: 0, y: 0.5, z: 0 } },
                { name: 'Main Board', color: '#00FF00', position: { x: 0, y: 0, z: 0 } },
                { name: 'Battery Pack', color: '#FF00FF', position: { x: 0, y: -0.5, z: 0 } },
                { name: 'Camera Module', color: '#FF0000', position: { x: 0.3, y: 0.3, z: 0.3 } },
                { name: 'Speaker', color: '#808080', position: { x: -0.3, y: 0.3, z: 0.3 } }
            ]
        }
    };

    const startAR = async () => {
        setIsLoading(true);
        try {
            // Y√™u c·∫ßu quy·ªÅn truy c·∫≠p camera
            const stream = await navigator.mediaDevices.getUserMedia({ 
                video: { facingMode: 'environment' } 
            });
            
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
            }
            
            setIsARMode(true);
            setIsVRMode(false);
        } catch (error) {
            console.error('Error accessing camera:', error);
            alert('Kh√¥ng th·ªÉ truy c·∫≠p camera. Vui l√≤ng ki·ªÉm tra quy·ªÅn truy c·∫≠p.');
        }
        setIsLoading(false);
    };

    const startVR = () => {
        setIsVRMode(true);
        setIsARMode(false);
        // VR mode s·∫Ω s·ª≠ d·ª•ng WebXR API
        if ('xr' in navigator) {
            navigator.xr.isSessionSupported('immersive-vr').then((supported) => {
                if (supported) {
                    console.log('VR ƒë∆∞·ª£c h·ªó tr·ª£');
                } else {
                    console.log('VR kh√¥ng ƒë∆∞·ª£c h·ªó tr·ª£');
                }
            });
        }
    };

    const exitARVR = () => {
        setIsARMode(false);
        setIsVRMode(false);
        if (videoRef.current && videoRef.current.srcObject) {
            const tracks = videoRef.current.srcObject.getTracks();
            tracks.forEach(track => track.stop());
        }
    };

    const render3DModel = () => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        const { width, height } = canvas;
        
        // Clear canvas
        ctx.clearRect(0, 0, width, height);
        
        // Draw 3D model components
        const currentModel = modelData[currentView];
        currentModel.components.forEach((component, index) => {
            const x = width / 2 + component.position.x * 100;
            const y = height / 2 + component.position.y * 100;
            const size = 30 + Math.sin(Date.now() / 1000 + index) * 5;
            
            ctx.fillStyle = component.color;
            ctx.beginPath();
            ctx.arc(x, y, size, 0, 2 * Math.PI);
            ctx.fill();
            
            // Draw component name
            ctx.fillStyle = '#000';
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';
            ctx.fillText(component.name, x, y + size + 15);
        });
    };

    useEffect(() => {
        if (isARMode || isVRMode) {
            const interval = setInterval(render3DModel, 100);
            return () => clearInterval(interval);
        }
    }, [isARMode, isVRMode, currentView]);

    return (
        <div className="ar-viewer">
            <div className="ar-viewer-header">
                <h2>AR/VR Viewer - {product?.name}</h2>
                <p>Xem c·∫•u tr√∫c thi·∫øt b·ªã v·ªõi c√¥ng ngh·ªá AR/VR</p>
            </div>

            {!isARMode && !isVRMode ? (
                <div className="ar-viewer-controls">
                    <div className="view-modes">
                        <h3>Ch·ªçn ch·∫ø ƒë·ªô xem:</h3>
                        <div className="mode-buttons">
                            <button 
                                className={`mode-btn ${currentView === 'exterior' ? 'active' : ''}`}
                                onClick={() => setCurrentView('exterior')}
                            >
                                <span className="mode-icon">üì±</span>
                                B√™n ngo√†i
                            </button>
                            <button 
                                className={`mode-btn ${currentView === 'interior' ? 'active' : ''}`}
                                onClick={() => setCurrentView('interior')}
                            >
                                <span className="mode-icon">üîß</span>
                                B√™n trong
                            </button>
                            <button 
                                className={`mode-btn ${currentView === 'exploded' ? 'active' : ''}`}
                                onClick={() => setCurrentView('exploded')}
                            >
                                <span className="mode-icon">üí•</span>
                                T√°ch r·ªùi
                            </button>
                        </div>
                    </div>

                    <div className="ar-vr-buttons">
                        <button 
                            className="ar-btn"
                            onClick={startAR}
                            disabled={isLoading}
                        >
                            <span className="btn-icon">üì∑</span>
                            B·∫Øt ƒë·∫ßu AR
                        </button>
                        <button 
                            className="vr-btn"
                            onClick={startVR}
                        >
                            <span className="btn-icon">ü•Ω</span>
                            B·∫Øt ƒë·∫ßu VR
                        </button>
                    </div>

                    <div className="model-preview">
                        <h3>{modelData[currentView].name}</h3>
                        <p>{modelData[currentView].description}</p>
                        <div className="components-list">
                            {modelData[currentView].components.map((component, index) => (
                                <div key={index} className="component-item">
                                    <div 
                                        className="component-color" 
                                        style={{ backgroundColor: component.color }}
                                    ></div>
                                    <span>{component.name}</span>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ) : (
                <div className="ar-vr-viewport">
                    {isARMode && (
                        <div className="ar-viewport">
                            <video 
                                ref={videoRef}
                                autoPlay 
                                playsInline 
                                className="camera-feed"
                            />
                            <canvas 
                                ref={canvasRef}
                                className="ar-overlay"
                                width={640}
                                height={480}
                            />
                        </div>
                    )}
                    
                    {isVRMode && (
                        <div className="vr-viewport">
                            <canvas 
                                ref={canvasRef}
                                className="vr-canvas"
                                width={800}
                                height={600}
                            />
                        </div>
                    )}

                    <div className="ar-vr-controls">
                        <div className="view-controls">
                            <button 
                                className={`control-btn ${currentView === 'exterior' ? 'active' : ''}`}
                                onClick={() => setCurrentView('exterior')}
                            >
                                üì±
                            </button>
                            <button 
                                className={`control-btn ${currentView === 'interior' ? 'active' : ''}`}
                                onClick={() => setCurrentView('interior')}
                            >
                                üîß
                            </button>
                            <button 
                                className={`control-btn ${currentView === 'exploded' ? 'active' : ''}`}
                                onClick={() => setCurrentView('exploded')}
                            >
                                üí•
                            </button>
                        </div>
                        
                        <button className="exit-btn" onClick={exitARVR}>
                            <span className="btn-icon">‚ùå</span>
                            Tho√°t
                        </button>
                    </div>

                    <div className="ar-vr-instructions">
                        <h4>H∆∞·ªõng d·∫´n s·ª≠ d·ª•ng:</h4>
                        <ul>
                            <li>Di chuy·ªÉn thi·∫øt b·ªã ƒë·ªÉ xem c√°c g√≥c ƒë·ªô kh√°c nhau</li>
                            <li>Ch·∫°m v√†o c√°c b·ªô ph·∫≠n ƒë·ªÉ xem th√¥ng tin chi ti·∫øt</li>
                            <li>S·ª≠ d·ª•ng c√°c n√∫t ƒë·ªÉ chuy·ªÉn ƒë·ªïi ch·∫ø ƒë·ªô xem</li>
                        </ul>
                    </div>
                </div>
            )}

            {isLoading && (
                <div className="loading-overlay">
                    <div className="loading-spinner"></div>
                    <p>ƒêang kh·ªüi t·∫°o AR...</p>
                </div>
            )}
        </div>
    );
}

export default ARViewer;
